INSERT INTO hadiths (id, arabic_text, kazakh_text, source) VALUES
(1, 'إِنَّ الدِّينَ عِندَ اللّهِ الإِسْلاَمُ', 'Шын мәнінде, Алланың алдындағы дін – Ислам.', '(Әли Имран 3:19)'),
(2, 'قُلْ هُوَ اللَّهُ أَحَدٌ', 'Айт: Ол – Алла, жалғыз.', '(Ыхылас 112:1)'),
(3, 'وَمَا خَلَقْتُ الْجِنَّ وَالْإِنسَ إِلَّا لِيَعْبُدُونِ', 'Мен жындар мен адамдарды Өзіме құлшылық ету үшін жараттым.', '(Зәрият 51:56)'),
(4, 'اللّهُ لاَ إِلَـهَ إِلاَّ هُوَ الْحَيُّ الْقَيُّومُ', 'Алладан басқа құдай жоқ. Ол – Тірі, Тұрақты.', '(Бақара 2:255)'),
(5, 'إِنَّمَا الْأَعْمَالُ بِالنِّيَّاتِ', 'Істер тек ниетке байланысты.', '(Бухари, Муслим)'),
(6, 'لَا يُؤْمِنُ أَحَدُكُمْ حَتَّى يُحِبَّ لِأَخِيهِ مَا يُحِبُّ لِنَفْسِهِ', 'Сендердің ешбірің өзіне қалағанын бауырына қаламайынша иман келтірмейді.', '(Бухари, Муслим)'),
(7, 'مَنْ صَمَتَ نَجَا', 'Кім үндемесе – құтылды.', '(Тирмизи)'),
(8, 'أَفْضَلُ الْأَعْمَالِ إِيمَانٌ بِاللَّهِ', 'Істердің ең абзалы – Аллаға иман келтіру.', '(Бухари)'),
(9, 'إِنَّ اللَّهَ يُحِبُّ الْمُحْسِنِينَ', 'Расында, Алла ихсан иелерін жақсы көреді.', '(Бақара 2:195)'),
(10, 'وَتَوَكَّلْ عَلَى اللَّهِ وَكَفَى بِاللَّهِ وَكِيلًا', 'Аллаға тәуекел ет! Ол – жеткілікті қамқоршы.', '(Ахзаб 33:3)'),
(11, 'الرِّضَا بِالْقَضَاءِ نُورُ الْإِيمَانِ', 'Тағдырға разы болу – иманның нұры.', '(Байхақи)'),
(12, 'إِنَّ اللَّهَ غَفُورٌ رَّحِيمٌ', 'Расында, Алла – өте кешірімді, ерекше мейірімді.', '(Бақара 2:173)'),
(13, 'التَّقْوَى هَا هُنَا', 'Тақуалық – міне, осында (жүрекке көрсетті).', '(Муслим)'),
(14, 'مَنْ حَسُنَ إِسْلَامُهُ تَرَكَ مَا لَا يَعْنِيهِ', 'Кімнің Исламы көркем болса, ол өзіне қатысы жоқ нәрседен тыйылады.', '(Тирмизи)'),
(15, 'وَاعْبُدْ رَبَّكَ حَتَّى يَأْتِيَكَ الْيَقِينُ', 'Раббыңа ғибадат ет, ажал жеткенше.', '(Хижр 15:99)'),
(16, 'لَا تَحْزَنْ إِنَّ اللَّهَ مَعَنَا', 'Қайғырма! Шын мәнінде, Алла біздің қасымызда.', '(Тәубе 9:40)'),
(17, 'الدُّعَاءُ هُوَ الْعِبَادَةُ', 'Дұға – құлшылықтың өзегі.', '(Тирмизи)'),
(18, 'إِنَّ اللَّهَ مَعَ الصَّابِرِينَ', 'Алла сабыр етушілермен бірге.', '(Бақара 2:153)'),
(19, 'التَّبَسُّمُ فِي وَجْهِ أَخِيكَ صَدَقَةٌ', 'Бауырыңа күлімдеу – садақа.', '(Тирмизи)'),
(20, 'مَنْ يَتَوَكَّلْ عَلَى اللَّهِ فَهُوَ حَسْبُهُ', 'Кім Аллаға тәуекел етсе – Ол оған жеткілікті.', '(Талақ 65:3)'),
(21, 'خَيْرُكُمْ مَنْ تَعَلَّمَ الْقُرْآنَ وَعَلَّمَهُ', 'Сендердің ең жақсыларың – Құранды үйреніп, оны үйреткендер.', '(Бухари)'),
(22, 'الطُّهُورُ شَطْرُ الْإِيمَانِ', 'Тазалық – иманның жартысы.', '(Муслим)'),
(23, 'وَاذْكُر رَّبَّكَ كَثِيرًا', 'Раббыңды көп еске ал.', '(Кәһф 18:24)'),
(24, 'لَنْ يُؤْمِنَ أَحَدُكُمْ حَتَّى أَكُونَ أَحَبَّ إِلَيْهِ', 'Мен оған бәрінен сүйіктірек болмайынша ешкім иман келтірмейді.', '(Бухари, Муслим)'),
(25, 'وَمَا أَرْسَلْنَاكَ إِلَّا رَحْمَةً لِّلْعَالَمِينَ', 'Біз сені әлемдерге рақым етіп жібердік.', '(Әнбия 21:107)'),
(26, 'خَيْرُ النَّاسِ أَنْفَعُهُمْ لِلنَّاسِ', 'Адамдардың ең жақсысы – адамдарға ең пайдалысы.', '(Табарани)'),
(27, 'مَنْ يُؤْمِنْ بِاللَّهِ وَالْيَوْمِ الآخِرِ فَلْيَقُلْ خَيْرًا أَوْ لِيَصْمُتْ', 'Кім Алла мен Ақырет күніне иман келтірсе, жақсы сөйлесін немесе үндемесін.', '(Бухари, Муслим)'),
(28, 'إِنَّ اللَّهَ لَا يُغَيِّرُ مَا بِقَوْمٍ', 'Алла бір қауымның жағдайын, олар өздерін өзгертпейінше, өзгертпейді.', '(Рағд 13:11)'),
(29, 'الرَّاحِمُونَ يَرْحَمُهُمُ الرَّحْمَٰنُ', 'Мейірімділерге Рахман мейірім етеді.', '(Тирмизи)'),
(30, 'وَعَجِلْتُ إِلَيْكَ رَبِّ لِتَرْضَى', 'Раббым! Сен риза болуың үшін асықтым.', '(Таһа 20:84)'),
(31, 'إِنَّ اللَّهَ يُحِبُّ التَّوَّابِينَ', 'Алла тәубе етушілерді жақсы көреді.', '(Бақара 2:222)'),
(32, 'كَادَ الْفَقْرُ أَنْ يَكُونَ كُفْرًا', 'Кедейлік – күпірлікке жақын.', '(Байхақи)'),
(33, 'لَا تَغْضَبْ', 'Ашуланба!', '(Бухари)'),
(34, 'مَنْ عَرَفَ نَفْسَهُ فَقَدْ عَرَفَ رَبَّهُ', 'Кім өзін таныса, Раббысын таниды.', '(Суфиян ас-Саури)'),
(35, 'إِنَّ أَكْرَمَكُمْ عِندَ اللَّهِ أَتْقَاكُمْ', 'Алланың алдында ең ардақтысы – ең тақуасы.', '(Хужурат 49:13)'),
(36, 'أَنَا سَيِّدُ وَلَدِ آدَمَ يَوْمَ الْقِيَامَةِ', 'Мен Қиямет күні Адам ұрпағының мырзасымын.', '(Муслим)'),
(37, 'كَانَ خُلُقُهُ الْقُرْآنَ', 'Оның мінезі – Құран еді.', '(Айша, Муслим)'),
(38, 'وَإِنَّكَ لَعَلَى خُلُقٍ عَظِيمٍ', 'Расында, сен ұлы мінез иесісің.', '(Қалам 68:4)'),
(39, 'أَكْمَلُ الْمُؤْمِنِينَ إِيمَانًا أَحْسَنُهُمْ خُلُقًا', 'Иманы ең толық мүмін – мінезі ең көркемі.', '(Тирмизи)'),
(40, 'سَكِينَةٌ فِي الْقَلْبِ مِنَ الْإِيمَانِ', 'Жүректегі тыныштық – иманнан.', '(Ибн Мajah)')
